import React, {
  Fragment,
  useState
} from 'react';

const Todo = props => {
  const inputState = useState('');

  const inputChangeHandler = (event) => {
    inputState[1](event.target.value)
  }

  return (<Fragment>
    <input
      type="text"
      placeholder="Todo"
      onChange={inputChangeHandler}
      value={inputState[0]}
    />
      <button type="button">Add</button>
    <ul/>

  </Fragment>)
}

export default Todo;
